import LineGradient from '../components/LineGradiant'
import { useForm } from 'react-hook-form'
import { motion } from 'framer-motion'

const Contacts = () => {
	const {
		register,
		trigger,
		formState: { errors },
	} = useForm()
	const onSubmit = async e => {
		const isValid = await trigger()
		if (!isValid) {
			e.preventDefault()
		}
	}
	return (
		<section id='контакты'>
			<motion.div
				className='md:w-1/3 text-right'
				initial='hidden'
				whileInView='visible'
				viewport={{ once: true, amount: 0.5 }}
				transition={{ duration: 0.5 }}
				variants={{
					hidden: { opacity: 0, x: -50 },
					visible: { opacity: 1, x: 0 },
				}}
			>
				<p className='font-playfair font-semibold text-4xl mb-5'>
					МОИ <span className='text-yellow'>КОНТАКТЫ</span>
				</p>
				<LineGradient width='max-w-[246px]' />
				<p className='mt-10 mb-7 font-playfair'>
					Здесь вы можете отправить мне письмо и связаться со мной
				</p>
			</motion.div>
		</section>
	)
}

export default Contacts
