import LineGradient from '../components/LineGradiant'
import { useForm } from 'react-hook-form'
import { motion } from 'framer-motion'

const Contacts = () => {
	const {
		register,
		trigger,
		formState: { errors },
	} = useForm()
	const onSubmit = async e => {
		const isValid = await trigger()
		if (!isValid) {
			e.preventDefault()
		}
	}
	return (
		<section id='контакты' className='py-48'>
			<motion.div
				initial='hidden'
				whileInView='visible'
				viewport={{ once: true, amount: 0.5 }}
				transition={{ duration: 0.5 }}
				variants={{
					hidden: { opacity: 0, x: -50 },
					visible: { opacity: 1, x: 0 },
				}}
			>
				<div className=''>
					<p className='font-playfair font-semibold text-4xl mb-5'>
						МОИ <span className='text-yellow'>КОНТАКТЫ</span>
					</p>

					<LineGradient width='max-w-[290px]' />

					<p className='mb-7 font-playfair text-lg'>
						Здесь вы можете отправить мне письмо и связаться со мной
					</p>
				</div>
			</motion.div>
			{/* ФОРМА ОТПРАВКИ И КАРТИНКА */}
			<div className='md:flex md: justify-between gap-16 mt-5'>
				<motion.div
					initial='hidden'
					whileInView='visible'
					viewport={{ once: true, amount: 0.5 }}
					transition={{ duration: 0.5 }}
					variants={{
						hidden: { opacity: 0, y: 50 },
						visible: { opacity: 1, y: 0 },
					}}
				>
					<img src='../assets/contact-image.jpeg' alt='contact' />
				</motion.div>

				<motion.div
					initial='hidden'
					whileInView='visible'
					viewport={{ once: true, amount: 0.5 }}
					transition={{ duration: 0.5 }}
					variants={{
						hidden: { opacity: 0, y: 50 },
						visible: { opacity: 1, y: 0 },
					}}
				>
					
				</motion.div>
			</div>
		</section>
	)
}

export default Contacts
