import { motion } from "framer-motion";
import LineGradient from '../components/LineGradiant'


const Testimonials = () =>
{
    // const isAboveMediumScreens = useMediaQuery('(min-width: 1060px)')
    const textimonialStyles = `mx-auto realtive max-w-[400px] h-[350px] flex flex-col
     justify-end pt-16 mt-48 before:absolute before:top-[-120px] before:-ml-[110px] before:left-1/2`;

    return (
			<section id='рекомендации' className='pt-32 pb-16'>
				{/* заголивок */}
				
					<motion.div
						className='md:w-1/3'
						initial='hidden'
						whileInView='visible'
						viewport={{ once: true, amount: 0.5 }}
						transition={{ duration: 0.5 }}
						variants={{
							hidden: { opacity: 0, x: -50 },
							visible: { opacity: 1, x: 0 },
						}}
					>
						<p className='font-playfair font-semibold text-4xl mb-5'>
							МОИ <span className='text-red'>РЕКОМЕНДАЦИИ</span>
						</p>
						<LineGradient width='w-[350px]' />
						<p className='mt-10 mb-7 font-playfair'>
							Здесь вы можете найти мои рекомендации довольных клиентов
						</p>
					</motion.div>
				
			</section>
		)
}


export default Testimonials;
