import LineGradient from '../components/LineGradiant'
import { motion } from 'framer-motion'


const Project = ({title}) =>
{
	const overlayStyles = `absolute h-full w-full opacity-0 hover:opacity-90 transition duration-500
	bg-grey z-30 flex flex-col justify=center items-center text-center p-16 text-deep-blue`;
    const projectTitle = title.split(" ").join("-").toLowerCase();

    return (
			<motion.div
				variants={projectVariant}
				className='relative'
			>
				<div className={overlayStyles}>

				</div>
			</motion.div>
		)
}

const container =  {
	hidden: {},
    visible: {
        transition: {staggerChildren: 0.2}
    },
};
const projectVariant = {
    hidden: {opacity: 0, scale: 0.8},
    visible: {opacity: 1, scale: 1}
}
const Projects = () => {
	return (
		<section id='портфолио'>
			{/* заголовок */}
			<motion.div
				className='md:w-2/4 mx-auto text-center'
				initial='hidden'
				whileInView='visible'
				viewport={{ once: true, amount: 0.5 }}
				transition={{ duration: 0.5 }}
				variants={{
					hidden: { opacity: 0, y: -50 },
					visible: { opacity: 1, y: 0 },
				}}
			>
				<div className='mt-20'>
					<p className='font-playfair font-semibold text-4xl'>
						МОИ <span className='text-red'>ПРОЕКТЫ</span>
					</p>
					<div className='flex justify-center mt-5'>
						<LineGradient width='w-[810px]' />
					</div>
				</div>
				<p className='mt-10 mb-10 font-playfair'>
					Здесь вы можете увидеть часть моих выполненных проектов
				</p>
			</motion.div>

            {/* проекты сами */}
            <div className='flex justify-center'>
                <motion.div
				className='sm:grid sm:grid-cols-3'
				initial='hidden'
				whileInView='visible'
				viewport={{ once: true, amount: 0.5 }}
				variants={container}
			>
                {/* 1я колонна */}
                <div 
                className='font-playfair flex justify-center text-center items-center p-10 bg-red
                max-w-[400px] max-h-[400px] text-2xl font-semibold'
                >
                    НЕВЕРОЯТНЫЕ ПОЛЬЗОВАТЕЛЬСКИЕ ИНТЕРФЕЙСЫ 
                </div>
                <Project title = "Проект 1"/>
            </motion.div>
            </div>
		</section>
	)
}

export default Projects
