import { useEffect, useState } from "react";
import useMediaQiery from "./hooks/useMediaQuery";
import Navbar from "./scenes/Navbar"
import DotGroup from "./scenes/DotGroup";
import Landing from "./scenes/Landing";
import LineGradiant from "./components/LineGradiant"
import MySkills from "./components/MySkills"

function App() {
  const [selectedPage, setSelectedPage] = useState('главная');
  const [isTopOfPage, setIsTopOfPage] = useState(true)
  const isAboveMediumScreens = useMediaQiery("(min-width: 1060px)");


  useEffect(() => {
		const handleScroll = () => {
			if (window.scrollY === 0) {
				setIsTopOfPage(true)
				setSelectedPage('home')
			}
			if (window.scrollY !== 0) setIsTopOfPage(false)
		}
		window.addEventListener('scroll', handleScroll)
		return () => window.removeEventListener('scroll', handleScroll)
	}, [])
  return (
		<div className='app bg-deep-blue'>
			<Navbar
				selectedPage={selectedPage}
				setSelectedPage={setSelectedPage}
				isTopOfPage={isTopOfPage}
			/>
			<div className='w-5/6 mx-auto md:h-full'>
				{isAboveMediumScreens && (
					<DotGroup
						selectedPage={selectedPage}
						setSelectedPage={setSelectedPage}
					/>
				)}
				<Landing />
			</div>
			<LineGradiant />
			<div className='w-5/6 mx-auto md:h-full'>
				<MySkills/>
			</div>
		</div>
	)
}

export default App;
